import React, { Component, useState } from "react";
import axios from "axios";
import Temp from "./Temp";

class Geo extends Component {
  constructor(props) {
    super(props);
    this.state = {
      countryName: "",
      cityName: "",
      lat: null,
      lon: null,
    };
  }
  getGeoInfo = () => {
    axios
      .get("https://ipapi.co/json/")
      .then((response) => {
        let data = response.data;
        this.setState({
          countryName: data.country_name,
          cityName: data.city,
          latitude: data.latitude,
          longitude: data.longitude,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };
  componentDidMount() {
    this.getGeoInfo();
    this.getTempInfo();
  }

  //  TEMP STUFF

  getTempInfo = () => {
    axios
      .get(
        "http://api.openweathermap.org/data/2.5/weather?lat=49.203&lon=-122.9914&appid=8f61a1e09924a4c204d0927cfe0ff964"
        // `http://api.openweathermap.org/data/2.5/weather?lat=${this.state.latitude}&lon=${this.state.longitude}&appid=8f61a1e09924a4c204d0927cfe0ff964`
        // "http://api.openweathermap.org/data/2.5/weather?lat=${props.lat}&lon=${props.lon}&appid=8f61a1e09924a4c204d0927cfe0ff964"
      )
      .then((response) => {
        let data = response.data;
        console.log(data);
        this.setState({
          temp: data.main.temp,
          wind: data.wind.speed,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };
  render() {
    if (this.state.wind > 5) {
      <p>It's pretty windy.</p>;
    } else {
      (" ");
    }
    return (
      <div>
        <p>Country Name: {this.state.countryName}</p>
        <p>City Name: {this.state.cityName}</p>
        <p>Latitude: {this.state.latitude}</p>
        <p>Longitude: {this.state.longitude}</p>
        <p>Tempie: {this.state.temp}</p>
        <p>Windie: {this.state.wind}</p>
        if ({this.state.wind > 5}){<p>It's pretty windy.</p>} else{" "}
        {<p> It's not that windy</p>}
        {/* <Temp lat={this.state.latitude} lon={this.state.longitude} /> */}
      </div>
    );
  }
}

export default Geo;
