import React, { useState, useEffect } from "react";
import axios from "axios";
import { Nav } from "react-bootstrap";

const Temp = ({ lat, lon }) => {
  const [state, setState] = useState({});

  useEffect(() => {
    if (lat && lon) {
      axios
        .get(
          `http://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=8f61a1e09924a4c204d0927cfe0ff964`
        )
        .then((response) => {
          let data = response.data;
          console.log(data);
          setState({
            temp: data.main.temp,
            wind: data.wind.speed,
            precip: data.weather[0].description,
          });
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [lat, lon, setState]);
  const temp = Math.round(state.temp - 273.15);
  const wind = Math.round(state.wind * 3.6);
  if (temp < 0 || wind > 20) {
    return (
      <div>
        <div class="pa3 f1 lh-title">You should probably stay inside.</div>
        <div role="img" aria-label="Flower">
